mapper.select:
SELECT su.user_id, su.login_id, su.user_name, su.email, su.telephone, su.is_valid, su.insert_time, su.google_key, string_agg(distinct sd.name,'/') dept_name, string_agg(distinct sr.role_name,'/') role_name, string_agg(distinct sd.dept_name_key,'/') dept_name_key, string_agg(distinct sr.role_name_key,'/') role_name_key FROM sys_user su LEFT JOIN sys_user_dept sud ON su.user_id = sud.user_id AND sud.is_deleted = '1' LEFT JOIN sys_dept sd ON sud.dept_id = sd.dept_id AND sd.is_deleted = '1' left join sys_user_role sur on su.user_id = sur.user_id and sur.is_deleted = '1' left join sys_role sr on sur.role_id = sr.role_id and sr.is_deleted = '1' WHERE su.is_deleted = '1'
AND (su.user_name LIKE CONCAT( '%', #{userName}, '%' ) OR su.login_id LIKE CONCAT( '%', #{userName}, '%' ))
AND su.is_valid = #{isValid}
AND sud.dept_id = #{deptId}
AND sur.role_id = #{roleId}
and ${sql_filter}
GROUP BY su.user_id

select count(1)
from sys_user
where login_id = #{loginId}

    and user_id != #{userId}

and is_deleted = '0'
